// components/OptimizedImage.tsx
import Image, { ImageProps } from 'next/image';

export function OptimizedImage({
  src,
  alt,
  ...props
}: Pick<ImageProps, 'src' | 'alt'> & Omit<ImageProps, 'src' | 'alt'>) {
  return (
    <Image
      src={src}
      alt={alt}
      priority={false}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
      {...props}
    />
  );
}
